
public class HAHA {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//79 41 23
		double x=79.0000;
		double y=41.0000;
		double z=23.0000;
		double Result=(x+y+z);
		double Div1=(x-(x-1));
		double Div2=(y-(y-1));
		double Div3=(z-(z-1));
		double DivFinal=(Div1+Div2+Div3);
		double Final_Result=(Result)/DivFinal;
		System.out.print("Result="); System.out.print(Final_Result);
		
		
			
			
		
		
	}

}
